import React, { useState } from 'react';
import classNames from 'classnames';
import TodoList from './TodoList';


function MagnifierButton(props) {
  const [isMagnified, setIsMagnified] = useState(false);

  const toggleMagnification = () => {
    setIsMagnified(!isMagnified);
  };

  return (
    <div className={`magnifier-container ${isMagnified ? 'magnified' : ''}`}>
      <button className="magnifier-button" onClick={toggleMagnification}>
        {isMagnified ? 'Disable Magnification' : 'Enable Magnification'}
      </button>
      {/* Render your items here */}
      <div className="items">{TodoList}</div>
    </div>
  );
}


  
  
  export default MagnifierButton;
