import React from 'react';
import {Badge, Alert, Container, Row, Col } from 'reactstrap';

const Task = (props) => {
    const { children } = props;

    const [completed, setCompleted] = React.useState(false);

    const onCompletedChanged = (event) => {
        //console.log(event);

        const isChecked = event.target.checked;

        setCompleted(isChecked);
    };

    console.log(completed);

    const taskStyle = {
        textDecoration: completed ? 'line-through' : 'none'
    }





    return (
        <>
            <li>
                <span style={taskStyle}>{children}</span>
             <Badge color="primary">
                <input type='checkbox' name='completed' onChange={onCompletedChanged} />
                <Alert>
                    <br>
                    <Container>  
                        <Row>
                        <Col xs={12} sm={6} md={4}>
                        <h6 className="alert-heading"></h6>
                        Well Done ! </Col></Row> </Container>   
                        </br>
                </Alert>
                </Badge> 
            </li>
        </>
    );
}

export default Task;
