import React from 'react';
import {Badge, Alert } from 'reactstrap';

const Task = (props) => {
    const { children } = props;

    const [completed, setCompleted] = React.useState(false);

    const onCompletedChanged = (event) => {
        //console.log(event);

        const isChecked = event.target.checked;

        setCompleted(isChecked);
    };

    console.log(completed);

    const taskStyle = {
        textDecoration: completed ? 'line-through' : 'none'
    }





    return (
        <>

            <li><Badge color="primary">
                <span style={taskStyle}>{children}</span>
                             <input type='checkbox' name='completed' onChange={onCompletedChanged} />
                <Alert>
                    <br>
                    <h6 className="alert-heading">
                        Well Done !
                        </h6>
                        </br>
                </Alert>
                </Badge>
            </li> 
        </>
    );
}

export default Task;
<Container>
      <Row>
        <Col xs={12} sm={6} md={4}>
          <div style={{ fontSize: '16px', backgroundColor: '#f0f0f0', padding: '10px' }}>
            Well done!
          </div>
        </Col>
      </Row>
    </Container>
