import React from 'react';

import './assets/App.css';
import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';
import FunFactPage from './pages/FunFactPage';
import TodoListPage from './pages/TodoListPage';
import NotFoundPage from './pages/NotFoundPage';
import "bootstrap/dist/css/bootstrap.min.css";
import ContentSizer from './components/ContentSizer.js';


const  App = () => {
  const [contentSize, setContentSize] = useState('normal');

  const increaseSize = () => {
    setContentSize('large');
  };

  const decreaseSize = () => {
    setContentSize('normal');
  };

  return (
    <div className="App">
      <h1>Content Size Adjuster</h1>
      <button onClick={increaseSize}>Increase Content</button>
      <button onClick={decreaseSize}>Decrease Content</button>
      <ContentSizer contentSize={contentSize} />
      {/* Render other components using the same contentSize */}
      <ContentSizer />
      <BrowserRouter>
        <Routes>

          <Route path='fun-fact' element={<FunFactPage />} />
          <Route path='todo-list' element={<TodoListPage />} />
          <Route index element={<Navigate to='/todo-list' />} />

          <Route path='*' element={<NotFoundPage />} />
        </Routes>
      </BrowserRouter>
      </div>  
  );
}




export default App;
