import React from 'react';
import '../assets/App.css'; // Import the CSS for styling

function ContentSizer({ contentSize, setContentSize }) {
  return (
    <div className={`content-sizer ${contentSize}`}>
      <div className="content">
        {/* Render your content here */}
        {contentSize === 'large' ? (
          <p>This is large content.</p>
        ) : (
          <p>This is normal content.</p>
        )}
      </div>
    </div>
  );
}

export default ContentSizer;
