import React, {useState} from 'react';
import './MagnifierButton.css'
import TodoList from './TodoList';
import {Button } from 'reactstrap';

function MagnifierButton(props) {
    const [isMagnified, setIsMagnified] = React.useState(false); 
  
    const toggleMagnification = () => {
      setIsMagnified(!isMagnified);
    };
  
    const magnifierClass = classNames('magnifier-container', {
      'magnified': isMagnified,
    });
  
    return (
      <div className={magnifierClass}>
        <button type = 'button'className="btn btn-primary" onClick={toggleMagnification}> Magnification
          {isMagnified ? 'Disable Magnification' : 'Enable Magnification'}
        </button> 
        <Button color="dark" onclick={toggleMagnification}> Magnification</Button>
               {TodoList}
      </div>
    );
  }
  
  export default MagnifierButton;
